        [Parameter(Mandatory)]
        [ValidateScript({ $_.Length -gt 1 })]
        [string]$Name,

        [ValidateSet('SOURCEIP', 'COOKIEINSERT', 'RULE', 'NONE')]
        [string]$Persistencetype,

        [ValidateSet('SOURCEIP', 'NONE')]
