        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Dbslb,

        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Processlocal,

        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Httponlycookieflag,

        [string]$Cookiepassphrase,

        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Usesecuredpersistencecookie,

        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Useencryptedpersistencecookie,

        [string]$Literaladccookieattribute,

