        [ValidateScript({ $_.Length -gt 1 })]
        [string]$Aclname,

        [double]$Td,

        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Connfailover = 'DISABLED',

        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Sourceroutecache = 'DISABLED',

        [ValidateSet('ENABLED', 'DISABLED')]
        [string]$Processlocal = 'DISABLED',

        [Switch]$PassThru 
    )
    begin {
        Write-Verbose "Invoke-NSAddForwardingsession: Starting"
    }
    process {
        try {
